import sys
import decimal
import math

pi = "3."
input = sys.stdin.readline

decimal.getcontext().prec = 50
a, b, c = map(decimal.Decimal, input().split())

fac = [decimal.Decimal(1), decimal.Decimal(1)]

for i in range(2, 75):
    fac.append(fac[-1]*decimal.Decimal(i))

def sin(x):
    result = x
    idx = 0
    for i in range(3, 75, 2):
        term = pow(x, decimal.Decimal(i))/fac[i]
        if abs(term) < decimal.Decimal('1e-50'):
            break
        if idx%2 == 0:
            result -= term
        else:
            result += term
        idx += 1
    return result
def f(x):
    return a * x + b * decimal.Decimal(sin(x % (decimal.Decimal(2)*decimal.Decimal(pi))))

start, end = decimal.Decimal(-10**10), decimal.Decimal(10**10)
for i in range(1000):
    mid = (start+end)/decimal.Decimal(2)
    res = f(mid)
    if res > c:
        end = mid
    else:
        start = mid
res = mid.quantize(decimal.Decimal('0.000001'), decimal.ROUND_HALF_EVEN)
print(res)
